                $this->prefillerFirstRow = true;
                $child .= $this->getPrefillerChild($item['prefiller'],$finalPrefillerMaxIndex,$tempDocketId,$docketFieldCategory);
            }else{
                $this->prefillerFirstRow = false;
                $child .="</tr>";
            }
        }
        return $child;
    }


