    public function groupByTempletId($newDockeArray){
        $result = array();
        foreach ($newDockeArray as $data) {
            $id = $data['docketId'];
            if (isset($result[$id])) {
                $result[$id][] = $data;
            } else {
                $result[$id] = array($data);
            }
        }

        return $result;
    }
