                     $docketExportMapping->label = $exportCategory->title;
                     $docketExportMapping->csv_header = $exportCategory->title;
                     $docketExportMapping->is_show = 1;
                     $docketExportMapping->save();
                 }

               if($exportCategory->id == 11){
                   $values[] = array(
                       'csvHeader' =>"Fixed Value",
                       'label' => "Fixed Value",
